import Loading from "../utils/Loading"
import useUserData from "../utils/useUserData"
import { useTranslation } from "react-i18next"
import database from '../utils/database'
import { useState, useEffect, useCallback } from "react"
import Button from '../utils/Button'

function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    const { t } = useTranslation();

    const [ allUsersData, setAllUsersData ] = useState([]);

    const [ userSearch, setUserSearch ] = useState('');

    useEffect(() => {
        async function loadData () {
            database.readDirectory('/users').then(obj => 
                setAllUsersData(Object.entries(obj)));
        };
        loadData();
    }, []);

    const basculerAdmin = useCallback((id, adminActuel) => {
        setAllUsersData(allUsersData.filter(([ident, _]) => ident === id).map(([ id, {admin, ...rest} ]) => {
            return [ id, { admin: !admin, ...rest}];
        }));
        database.setUserData(id, 'admin', !adminActuel)
    }, [allUsersData]);

    const search = useCallback(event => {
        setUserSearch(event.target.value);
    }, [])

    if (isLoadingAuth) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (isLoadedUser && !userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    const {
        translationPdp,
        translationPseudo,
        translationAdmin,
        translationOui,
        translationNon,
        translationBasculer,
        translationUserList,
        translationRechercher
    } = t('admin_page_users', {returnObjects: true})

    return (
        <>
        <h3>{translationUserList}</h3>
        <input onChange={search} value={userSearch} defaultValue={translationRechercher} style={{margin: '15px'}}/>
        {userSearch}
        <table className="pretty-table">
            <thead>
                <tr>
                    <td>{translationPdp}</td>
                    <td>{translationPseudo}</td>
                    <td>{translationAdmin}</td>
                    <td></td>
                </tr>
            </thead>
            <tbody>
                {(allUsersData && (
                    allUsersData.map(([id, {userName, userPicture, admin}]) => {
                        return (
                            <tr key={id}>
                                <td><img
                                        src={userPicture} alt=""
                                        referrerPolicy="no-referrer"
                                        style={{borderRadius: '15px', width: '40px', verticalAlign: 'middle'}}
                                    />
                                </td>
                                <td>{userName}</td>
                                <td>{admin ? translationOui : translationNon}</td>
                                <td><Button style={{paddingTop: '4px',
                                                    paddingBottom: '4px',
                                                    paddingRight: '10px',
                                                    paddingLeft: '10px',
                                                    verticalAlign: 'middle'}}
                                            onClick={() => basculerAdmin(id, admin)}>
                                        {translationBasculer}
                                    </Button>
                                </td>
                            </tr>
                        )
                    })
                ))}
            </tbody>
        </table>
        </>
    );
}

export default Admin;
