import { initializeApp } from "firebase/app";
import { getDatabase, set, ref, onValue } from "firebase/database"

const firebaseConfig = {
  apiKey: "AIzaSyAN-PNbJlAmJ_zQ6Juyn1ATWAo4PRGxaCY",
  authDomain: "coman-app-react.firebaseapp.com",
  projectId: "coman-app-react",
  storageBucket: "coman-app-react.appspot.com",
  messagingSenderId: "443294717061",
  appId: "1:443294717061:web:1f6110e2d9c8ab90c5c5ec",
  databaseURL: "https://coman-app-react-default-rtdb.europe-west1.firebasedatabase.app"
};

const app = initializeApp(firebaseConfig);

class Database {
    constructor() {
        this.database = getDatabase(app);
    }

    async setItem(id, item) {
        await set(ref(this.database, `qqch/_${id}`), item)
    }

    async readItem(id) {
        return new Promise((resolve, _) => {
            const reference = ref(this.database, `qqch/_${id}`);
            onValue(reference, (el) => {
                return resolve(el.val());
            })
        })
    }
}

const database = new Database();

export default database;
