function Scrollbar() {
	return <div className='scrollbar'></div>;
}

export default function withScrollbar(Child) {
	const log = (liste) => {
		liste.forEach((item) => {
			console.log({ item });
		});
	};
	return () => (
		<div className='scrollable'>
			<Child ref={(node) => node && log([node.clientHeight, node.offsetHeight, node.scrollHeight, node.offsetTop])} />
			<Scrollbar />
		</div>
	);
}
