import AdminUsersManager from "./AdminUsersManager"
import useUserData from "../utils/useUserData"
import Loading from "../utils/Loading"
import { useTranslation } from "react-i18next"
import { CSSTransition } from 'react-transition-group'
import { useCallback, useState } from "react"
import { WrapFondu } from "../utils/appTemplates"


function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    const [ toShow, setToShow ] = useState(false);

    const { t } = useTranslation();

    const toggleShow = useCallback(() => {
        setToShow(toShow => !toShow);
    }, [])

    if (isLoadingAuth || !isLoadedUser) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (isLoadedUser && !userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    return <>
        <button onClick={toggleShow}>LOL</button>
        <WrapFondu app={<AdminUsersManager />} show={toShow} />
        LOL
        </>
}

export default Admin;
