import React from "react";
import { motion } from "framer-motion";
import { useMemo } from "react";
import { useCallback, useState } from "react";
import { AnimatePresence } from "framer-motion";

import Button from "./Button";

function LeftToRightComponents({children}) {

    const [ selected, setSelected ] = useState(0);

    const max = useMemo(() => React.Children.count(children) - 1, [children]);

    const next = useCallback(() => {
        setSelected(selected => selected < max ? selected + 1 : selected);
    }, [max])

    const previous = useCallback(() => {
        setSelected(selected => selected > 0 ? selected - 1 : selected);
    }, [])

    const selector = useCallback(() => {
        return (
            <>
            <Button onClick={previous}>Précédent</Button>
            <Button onClick={next}>Suivant</Button>
            </>
        )
    }, [next, previous])
    
    return children && (
        <>
        {selector()}
        {
            React.Children.map(children, (child, i) => {
                return <AnimatePresence exitBeforeEnter><motion.div
                    initial={{x: '-100vw'}}
                    animate={{x: 0}}
                    exit={{x: '100vw'}}
                    transition={{duration: 1}}
                    key={i}
                >
                    {selected === i && <>{i} et {child}</>}
                </motion.div></AnimatePresence>
            })
        }
        </>
    );

}

export default LeftToRightComponents;
