import { useRef } from "react";
import { useState } from "react";

import LeftToRightComponents from "../utils/components/LeftToRightComponents";

function Home() {
	const [next, bindNext] = useState(() => () => {});
	const [previous, bindPrevious] = useState(() => () => {});

	const [selected, setSelected] = useState(null);

	const childRef = useRef();

	return (
		<div style={{ textAlign: "center" }}>
			<button
				onClick={previous}
				style={{ marginRight: "15px", width: "140px" }}
				{...(childRef.current && childRef.current.isFirst() && { disabled: true })}>
				Précédent
			</button>
			<button
				onClick={next}
				style={{ marginLeft: "15px", width: "140px" }}
				{...(childRef.current && childRef.current.isLast() && { disabled: true })}>
				Suivant
			</button>
			<LeftToRightComponents
				height="50vh"
				alignItems="top"
				ref={childRef}
				bindNext={bindNext}
				bindPrevious={bindPrevious}
				durationInMs={200}
				updateSelectedOnParent={(res) => setSelected(res.join("/"))}>
				<>Page 1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</>
				<>Page 2</>
				<>Page 3</>
			</LeftToRightComponents>
			Page {selected}
		</div>
	);
}

export default Home;
