import React from "react";
import { DEFAULT_ANIMATION_LENGTH } from "./FadeProps";

class WidthAnimatedTextDiv extends React.Component {
	constructor({ setSetter, children }) {
		super();
		this.state = {
			text: children,
		};
		setSetter(() => (text) => this.changeText(text));
		this.textRef = React.createRef();
	}

	changeText(text) {
		this.setState(
			{
				text: null,
			},
			() => {
				if (this.textRef.current) {
					this.textRef.current.style.width = `${this.textRef.current.scrollWidth}px`;
				}
				setTimeout(() => {
					this.setState(
						{
							text: text,
						},
						() => {
							if (this.textRef.current) {
								console.log(this.textRef.current.scrollWidth);
								this.textRef.current.style.width = `${this.textRef.current.scrollWidth}px`;
							}
						}
					);
				}, DEFAULT_ANIMATION_LENGTH);
			}
		);
	}

	render() {
		return (
			<>
				{this.state.text && (
					<div ref={this.textRef} className='width-animated-text-div'>
						{this.state.text}
					</div>
				)}
			</>
		);
	}
}

export default WidthAnimatedTextDiv;
