import { useCallback, useState } from "react";
import { useTranslation } from "react-i18next";

import { motion } from "framer-motion";
import Button from "../utils/components/Button";

function Home() {
	const [number, setNumber] = useState(1);
	const [hasClickedOnce, setHasClickedOnce] = useState(false);
	const { t } = useTranslation();

	let onClick = useCallback((_) => {
		setNumber((number) => number + 1);
		setHasClickedOnce(true);
	}, []);

	return (
		<div style={{ textAlign: "center" }}>
			<h1 style={{width: '60%'}}>L'accueil est pour l'instant vide.
				Tu peux toutefois toujours t'amuser avec le bouton ci-apr√®s.</h1>
			<Button onClick={onClick}>Actuel : {number}</Button>

			{hasClickedOnce ? (
				<motion.div
					animate={{ y: "20px", opacity: 1, scale: 1 }}
					initial={{ y: "+80vh", opacity: 0, scale: 0 }}
					transition={{ duration: 1.5 }}>
					{t("home_no_idea_message")}
				</motion.div>
			) : (
				""
			)}
		</div>
	);
}

export default Home;
