import React, { Suspense, useEffect } from "react";
import ReactDOM from "react-dom";
import { useLocation } from "react-router";
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import { AnimatePresence } from "framer-motion";
import { Auth0Provider } from "@auth0/auth0-react";
import { useMediaQuery } from "react-responsive";
import { changeLanguage } from "i18next";

import "./styles/style.scss";
import NavigationBar from "./NavigationBar";
import { WrapFondu } from "./utils/functions/appTemplates";
import database from "./utils/database/database";
import pagesList from "./pages/pagesList";
import updateCSS from "./styles/responsive";
import Loading from "./utils/components/Loading";
import "./languages/i18n";
import useUserData from "./utils/hooks/useUserData";

const auth0Domain = process.env.REACT_APP_AUTH0_DOMAIN;
const auth0ClientId = process.env.REACT_APP_AUTH0_CLIENTID;

function Page() {
	const isPortrait = useMediaQuery({ query: "(orientation: portrait)" });

	window.addEventListener("beforeunload", (_) => {
		database.disconnect();
	});

	const location = useLocation();

	const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

	useEffect(() => {
		if (!isLoadingAuth && isAuthenticated && isLoadedUser) {
			changeLanguage(userData["language"]);
		}
	}, [isLoadingAuth, isLoadedUser, isAuthenticated, userData]);

	useEffect(() => {
		if (isPortrait) {
			updateCSS("portrait");
		} else {
			updateCSS("paysage");
		}
	}, [isPortrait]);

	let getPagesRoutes = () => {
		return pagesList.map(({ name, path, app }) => {
			return (
				<Route
					key={name}
					path={path}
					element={
						<div className='page'>
							<NavigationBar currentPageName={name} pagesList={pagesList} />
							<WrapFondu app={<div className='app-container'>{app}</div>} />
						</div>
					}
				/>
			);
		});
	};

	return (
		<AnimatePresence exitBeforeEnter>
			<Routes location={location} key={location.pathname}>
				<Route exact path='/' element={<Navigate to='/home' state />} />

				{getPagesRoutes()}

				<Route path='*' element={<Navigate to='/pageNotFound' state />} />
			</Routes>
		</AnimatePresence>
	);
}

const rootElement = document.getElementById("root");
ReactDOM.render(
	<BrowserRouter>
		<Auth0Provider domain={auth0Domain} clientId={auth0ClientId} redirectUri={window.location.origin + "/home"}>
			<Suspense fallback={<Loading />}>
				<Page />
			</Suspense>
		</Auth0Provider>
	</BrowserRouter>,
	rootElement
);
