import { useEffect, useState } from "react"
import "./style.css"
import { NavLink } from "react-router-dom";
import { motion } from "framer-motion";

function NavigationBar(props) {
    
    const pagesList = props.pagesList;
    const [ currentPageName, setCurrentPageName ] = useState(props.currentPageName);

    useEffect(() => {
        setCurrentPageName(props.currentPageName)
    }, [props.currentPageName])

    return (
        <motion.div
            exit={{y: '-102%'}}
            animate={{y: -2}}
            initial={{y: '-102%'}}
        >
            <ul className="navigation-bar">
                {
                    pagesList.slice().reverse().map(({name, path}) => {
                        return (
                            <li key={name} className={name === currentPageName ? "active" : ""}>
                                <NavLink to={path}>
                                    {name}
                                </NavLink>
                            </li>
                        )
                    })
                }
            </ul>
        </motion.div>
    )

}

export default NavigationBar;
