import { changeLanguage } from "i18next";

import info from "../../languages/info";
import database from "../../utils/database/database";
import Selection from "../../utils/components/Selection";

const options = info.map(({language, label}) => {
	return { value: language, label };
});

const LanguageSelectorField = ({ userData, userID }) => {
	const handleChange = ({ value }) => {
		changeLanguage(value);
		database.setUserData(userID, "language", value);
	};

	return (
		<Selection
			options={options}
			defaultOption={options.find((lang) => lang.value === userData.language)}
			handleChange={handleChange}
		/>
	);
};

export default LanguageSelectorField;
