function CopyText({ children, className = '', ...rest }) {
	return (
		<span {...rest} className={`copy-text ${className}`} onClick={() => navigator.clipboard.writeText(children).catch(() => {})}>
			{children}
		</span>
	);
}

export default CopyText;
