import { useAuth0 } from "@auth0/auth0-react";
import { useState, useEffect, Fragment } from "react";
import database from "../utils/database";
import Loading from "../utils/Loading";

function Settings() {
    
    const { user, isLoading, isAuthenticated } = useAuth0();
    const [ userData, setUserData ] = useState({ isLoaded: false, data: {} })

    if (isLoading) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <Fragment>Veuillez vous connecter pour modifier vos paramètres.</Fragment>
    }

    // L'utilisateur est authentifié

    const userID = user.sub;

    
    useEffect(() => {
        async function loadUserData () {
            database.searchItems("users", {userID: userID})
                .then(userData => {
                    setUserData({
                        isLoaded: true,
                        data: userData
                    })
                })
                .catch(_ => {
                    setUserData({isLoaded: true, data: {}})
                })
        }
        loadUserData();
    })

    return (
        !userData.isLoaded ? <Loading /> : (
            <div style={{textAlign: 'center'}}>
                {console.log(userData.data)}
            </div>
        )
    )

}

export default Settings;
