import Loading from "../utils/Loading"
import useUserData from "../utils/useUserData"
import LanguageSelectorField from "./settings/Language"
import { useTranslation } from 'react-i18next'
import axios from 'axios'
import { useEffect, useState } from "react"

function SettingsField({label, component}) {
    return (
        <div className="settings-field">
            <span className="settings-field-label">{label}</span>
            <div className="settings-field-component-container">
                <div className="settings-field-component">
                    {component}
                </div>
            </div>
        </div>
    )
}

function Settings() {
    
    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData, userID } = useUserData();

    const { t } = useTranslation();

    const [ machinesData, setMachinesData ] = useState({
        isLoaded: false,
        data: {}
    })

    useEffect(() => {
        axios.get('https://coman-machines-api.herokuapp.com')
        .then(reponse => {
            setMachinesData({
                isLoaded: true,
                data: reponse.data.machine_info_status.machine_list
            })
        })
    }, [])

    if (isLoadingAuth) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_settings')}</>
    }
        
    // L'utilisateur est authentifié
    return (
        !isLoadedUser ? <Loading /> : (
            <div className="settings-div">
                <SettingsField
                    label={t('settings_language_field_label')}
                    component={<LanguageSelectorField userData={userData} userID={userID} />}
                />
            </div>
        )
    )

}

export default Settings;
