import { useState, useEffect } from "react"
import Loading from "../utils/Loading"
import axios from "axios"
import { BsFillCheckSquareFill, BsFillStopwatchFill } from 'react-icons/bs'
import { useTranslation } from "react-i18next"

const num = "selecteur_machine";
const type = "nom_type";
const statut = "status";
const tempsRestant = "time_before_off";

function Machines() {

    const [ machinesData, setMachinesData ] = useState({
        isLoading: true,
        data: {}
    })

    const { t } = useTranslation();

    const { 
        washing_machine,
        dryer,
        number,
        type: typeText,
        status,
        time_remaining,
        update
    } = t('machines_page', {returnObjects: true})

    let loadData = () => {
        axios.get('https://coman-machines-api.herokuapp.com')
        .then(reponse => {
            setMachinesData({
                isLoading: false,
                data: reponse.data.machine_info_status.machine_list
            })
        })
    }

    useEffect(() => {
        loadData();
    }, [])
    
    let timeFormat = (secondes) => {
        const minutes = Math.floor(secondes / 60);
        const newSecondes = secondes - minutes * 60;
        const minutesString = minutes < 10 ? `0${minutes}` : `${minutes}`;
        const newSecondesString = newSecondes < 10 ? `0${newSecondes}` : `${newSecondes}`;
        return minutesString + ":" + newSecondesString;
    }

    return machinesData.isLoading ? <Loading /> : (
        <>
        <table className="machines-table">
            <thead>
                <tr>
                    <td>{number}</td>
                    <td>{typeText}</td>
                    <td>{status}</td>
                    <td>{time_remaining}</td>
                </tr>
            </thead>
            <tbody>
                {machinesData.data.map(machine => {
                    return (
                        <tr key={machine[num]}>
                            <td>{machine[num]}</td>
                            <td>{machine[type].trim().startsWith('L') ? washing_machine : dryer}</td>
                            <td>
                                {machine[statut] === 1 ? 
                                    <BsFillCheckSquareFill style={{color: "var(--lighter-green)"}} /> : 
                                    <BsFillStopwatchFill style={{color: 'var(--orange)'}} />
                                }
                            </td>
                            <td>{machine[statut] === 1 ? "-" : timeFormat(machine[tempsRestant])}</td>
                        </tr>
                    )
                })}
            </tbody>
        </table>
        <button onClick={loadData} style={{marginTop: '15px'}}><span>{update}</span></button>
        </>
    );
}

export default Machines;
