import "./style.css"
import React from "react"
import ReactDOM from "react-dom"
import { useLocation } from "react-router"
import Home from "./pages/Home"
import Other from "./pages/Other"
import NavigationBar from "./NavigationBar"
import { BrowserRouter, Route, Routes, Navigate } from "react-router-dom";
import { CSSTransition, TransitionGroup } from "react-transition-group";

function Page() {

  const location = useLocation();

  const pagesList = [
    {
        name: "Home",
        path: "/home",
        app: <Home />
    },
    {
        name: "Other",
        path: "/other",
        app: <Other />
    }
  ]

  let getPagesRoutes = () => {
    return (
      pagesList.map( ({ name, path, app }) => {
        return (
          <Route key={name} path={path} element={
            <div className="page">
              <div className="navbar"><NavigationBar currentPageName={name} pagesList={pagesList} /></div>
              <div className="app-container">{app}</div>
            </div>
          } />
        )
      })
    )
  }

  return (
      <BrowserRouter>
      <TransitionGroup>
        <CSSTransition classNames='fade' timeout={300}>
          <Routes>

            <Route exact path="/" element={<Navigate to="/home" state/>} />

            {getPagesRoutes()}

          </Routes>
        </CSSTransition>
      </TransitionGroup>
      </BrowserRouter>
  );
}

const rootElement = document.getElementById("root")
ReactDOM.render(<Page />, rootElement)
