import { useState, useEffect } from "react"
import Loading from "../utils/Loading"
import axios from "axios"

function Machines() {

    const [ machinesData, setMachinesData ] = useState({
        isLoading: true,
        data: {}
    })

    useEffect(() => {
        axios.get('https://coman-machines-api.herokuapp.com')
        .then(reponse => {
            setMachinesData({
                isLoading: false,
                data: reponse.data.machine_info_status.machine_list
            })
        })
    }, [])
    
    return machinesData.isLoading ? <Loading /> : (
        <> {JSON.stringify(machinesData.data)} </>
    );
}

export default Machines;
