import { useCallback, useState } from "react";
import { useForm } from "react-hook-form";
import { send } from "emailjs-com";

import useWindowDimensions from "../../../utils/hooks/useWindowDimensions";
import ModalContact from "./ModalContact";

export default function MeContacter({ isOpen, setIsOpen }) {
	const {
		register,
		handleSubmit,
		formState: { errors },
		reset,
	} = useForm();

	/*
	 0 - Initial
	 1 - Loading (sending the mail)
	 2 - Success
	-1 - Error
	*/
	const [step, setStep] = useState(0);

	const { width: winWidth } = useWindowDimensions();

	const submit = useCallback((data) => {
		console.log(data);
		// setStep(1);
		// Maybe put these constants in .env file
		// send("service_vll6ms8", "template_4i4v4tc", data, "user_bWOvJmKNGyBen5qbMmJm8")
		// 	.then(() => setStep(2))
		// 	.catch(() => setStep(-1));
	}, []);

	return (
		<ModalContact
			isOpen={isOpen}
			setIsOpen={setIsOpen}
			onClose={() => {
				setStep(0);
				reset();
			}}>
			{step === 0 ? (
				<form onSubmit={handleSubmit(submit)} className='formulaire'>
					<h3>Me contacter</h3>

					<div className='corps'>
						<label>Nom</label>
						<input
							{...register("from_name", { required: true })}
							placeholder='Nom'
							className={errors.name && "invalid"}
						/>
						<span className='error'>{errors.name && errors.name.message}</span>

						<label>Mail</label>
						<input
							{...register("from_email", {
								pattern: /^[A-Za-z0-9]+@[a-zA-Z0-9.]+\.[a-zA-Z]{2,4}$/i,
								required: "L'e-mail donnée n'est pas au bon format",
							})}
							placeholder='E-mail'
							className={errors.email && "invalid"}
						/>
						<span className='error'>{errors.email && errors.email.message}</span>

						<label>Message</label>
						<textarea
							{...register("message", { required: true })}
							placeholder='Message'
							className={errors.message && "invalid"}
							rows={5}
							style={{ width: winWidth * 0.2 }}
						/>
						<span className='error'>{errors.message && errors.message.message}</span>
					</div>

					<input type='submit' value={"Envoyer"} className='submit' />
				</form>
			) : step === 1 ? (
				<h3 style={{ padding: "50px" }}>Envoi en cours...</h3>
			) : step === 2 ? (
				<h3 style={{ padding: "50px" }}>Envoyé avec succès</h3>
			) : (
				<h3 style={{ padding: "50px" }}>Une erreur est survenue.</h3>
			)}
		</ModalContact>
	);
}
