import { useTranslation } from "react-i18next";
import { useState } from "react";
import FadeProps from "fade-props";

import AdminUsersManager from "./AdminUsersManager";
import useUserData from "../utils/hooks/useUserData";
import Loading from "../utils/components/Loading";
import ButtonsSelector from "../utils/components/ButtonsSelector";

function Admin() {
	const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

	const [selected, setSelected] = useState(null);

	const { t } = useTranslation();

	const { translationAdminUsers } = t("admin_page", { returnObjects: true });

	if (isLoadingAuth || !isLoadedUser) {
		return <Loading />;
	}

	if (!isAuthenticated) {
		return <>{t("unauthentified_message_admin_page")}</>;
	}

	if (isLoadedUser && !userData.admin) {
		return <>{t("unauthorized_message_admin_page")}</>;
	}

	return (
		<>
			<ButtonsSelector
				selected={selected}
				setSelected={setSelected}
				buttons={[translationAdminUsers, 'LOL']}
				style={{ marginBottom: "50px" }}
			/>
			<FadeProps>{selected === 0 && <AdminUsersManager />}</FadeProps>
		</>
	);
}

export default Admin;
