import { useMemo } from "react";

import useWindowDimensions from "../../../../utils/hooks/useWindowDimensions";

export default function JeuxComplexes() {
	const { width: winWidth } = useWindowDimensions();

	const style = useMemo(
		() => ({
			width: winWidth * 0.6,
		}),
		[winWidth]
	);

	return (
		<div style={style}>
			<span>
				J'aime les jeux que je considère complexes.
				<br />
				J'ai déjà parlé dans la section précédente des <souligne>énigmes</souligne>, que je vois comme un jeu complexe où le but
				est de trouver la solution de la manière la plus élégante et la plus rapide qui soit.
				<br />
				<br />
				Je joue maintenant depuis assez longtemps aux échecs.
			</span>
		</div>
	);
}
