import { useTranslation } from "react-i18next";
import { photoPro } from "../../../images";
import CopyText from "../../../utils/components/CopyText";
import { espace } from "../../../utils/others/shortcuts";

function Informations() {
	const { t } = useTranslation();

	const { adresse_mail, telephone, adresse_postale, description, copied_mail, copied_telephone, copied_adresse } = t("informations_page", { returnObjects: true });
	console.log(description);
	return (
		<div className='informations-container'>
			<div className='image-container'>
				<img
					src={photoPro}
					width='242px'
					height='322px'
					style={{ borderRadius: "50%", boxShadow: "0 0 15px black" }}
					alt=''
				/>
			</div>
			<div className='text-container'>
				<p className='left retour-ligne' style={{ fontSize: "calc(var(--page-font-size) * 1.05)" }}>
					{description}
				</p>
				<br />
				<br />
				<br />
				<br />
				<p className='left'>
					{adresse_mail}{t("espace_avant_ponctuation")}:{espace}
				</p>
				<br />
				<p className='left'>
					{telephone}{t("espace_avant_ponctuation")}:{espace}
				</p>
				<br />
				<div className='left inline'>
					{adresse_postale}{t("espace_avant_ponctuation")}:{espace}
				</div>
			</div>
		</div>
	);
}

export default Informations;
