import { useEffect, useState, Fragment } from "react"
import { useNavigate } from "react-router"
import { motion } from "framer-motion"
import { SiBandsintown } from "react-icons/si"
import Logo from './utils/Logo'
import { useAuth0 } from '@auth0/auth0-react'

function NavigationBar(props) {

    const navigate = useNavigate();
    const { user, isLoading, isAuthenticated } = useAuth0();
    
    const pagesList = props.pagesList;
    const [ currentPageName, setCurrentPageName ] = useState(props.currentPageName);

    useEffect(() => {
        setCurrentPageName(props.currentPageName)
    }, [props.currentPageName])

    return (<Fragment>
        <motion.div
        exit={{y: '-100%'}}
        animate={{y: 0}}
        initial={{y: '-100%'}}
        transition={{duration: 0.5, delay: 0.6}}
        >
            <div className="navigation-bar">
                <ul>
                    {/* Le logo de l'app */}
                    <li style={{float: 'left'}}>
                        <Logo />
                    </li>
                    <li className="right">LOL</li>
                    {/* La liste des pages Ã  droite */}
                    {   
                        pagesList.slice().reverse().filter(page => !page.hidden).map(({name, path, icon}) => {
                            return (
                                <li key={name}
                                className={name === currentPageName ? "active right" : "right"} onClick={() => navigate(path)} >
                                    <div className="nav-bar-item">
                                        <div className="nav-bar-item-icon">{icon}</div>
                                        <div className="nav-bar-item-text">{name}</div>
                                    </div>
                                </li>
                            )
                        })
                    }
                </ul>
            </div>
        </motion.div>
        <motion.div
        exit={{x: '+140%'}}
        animate={{x: 0}}
        initial={{x: '-180%'}}
        transition={{duration: 1.5}}
        >
            {
                currentPageName ? 
                    (
                        <div className="left-title">
                        <SiBandsintown style={{
                                transform: 'skew(-40deg)',
                                marginRight: '10vw',
                                fontSize: '50px',
                                color: 'var(--orange)'
                            }}/>
                            <span className="droit">{pagesList.find(page => page.name === currentPageName).title}</span>
                        </div>
                    )
                    :
                    ""
            }
        </motion.div>
        </Fragment>
    )

}

export default NavigationBar;
