import piano from "../images/piano.jpg";
import guitare from "../images/guitare.jpg";
import { useEffect } from "react";

function Element({ image, reRender, children }) {
	return (
		<div className='musique-element-div'>
			<img className='image' src={image} alt='' onLoad={reRender} />
			<div className='content'>{children}</div>
		</div>
	);
}

const Musique = () => {

	const [toReRender, setToReRender] = useState(0);

	const reRender = () => setToReRender(i => i+1);

	return (
		<div className='musique-elements-div'>
			<Element image={piano} reRender={reRender}>
				<span>10 ans de pratique</span>
				<span>Autodidacte</span>
			</Element>
			<Element image={guitare} reRender={reRender}>
				<span>Quelques mois de pratique</span>
			</Element>
		</div>
	);
};

export default Musique;
