import Loading from "../utils/Loading"
import useUserData from "../utils/useUserData"
import { useTranslation } from "react-i18next"
import database from '../utils/database'
import { useState, useEffect, useCallback } from "react"
import Button from '../utils/Button'
import { RiDeleteBack2Fill } from 'react-icons/ri'
import Highlighter from "react-highlight-words"

function AdminUsersManager() {

    // TODO: ADMIN PAGE WITH CHOICES
    // ONE CHOICE CALLS THIS COMPONENT (RENAMED TO 'Users')

    const { isLoadingAuth, isLoadedUser } = useUserData();

    const { t } = useTranslation();

    const [ allUsersData, setAllUsersData ] = useState([]);

    const [ userSearch, setUserSearch ] = useState('');

    useEffect(() => {
        async function loadData () {
            database.readDirectory('/users').then(obj => 
                setAllUsersData(Object.entries(obj)));
        };
        loadData();
    }, []);

    const basculerAdmin = useCallback((id, adminActuel) => {
        setAllUsersData(allUsersData.filter(([ident, _]) => ident === id).map(([ id, {admin, ...rest} ]) => {
            return [ id, { admin: !admin, ...rest}];
        }));
        database.setUserData(id, 'admin', !adminActuel)
    }, [allUsersData]);

    const search = useCallback(event => {
        setUserSearch(event.target.value);
    }, [])

    if (isLoadingAuth || !isLoadedUser) {
        return <Loading />
    }

    const {
        translationPdp,
        translationPseudo,
        translationAdmin,
        translationOui,
        translationNon,
        translationBasculer,
        translationUserList,
        translationRechercher
    } = t('admin_page_users', {returnObjects: true})

    return (
        <>
        <h3 style={{marginBottom: '20px'}}>{translationUserList}</h3>
        <div style={{display: 'flex', flexDirection: 'row', alignItems: 'center', flexFlow: 'row', marginBottom: '10px'}}>
            <input onChange={search} value={userSearch} placeholder={translationRechercher} style={{marginRight: '0'}}/>
            <RiDeleteBack2Fill style={{color: 'var(--orange)', cursor: 'pointer'}} onClick={() => setUserSearch('')}/>
        </div>
        <table className="pretty-table">
            <thead>
                <tr>
                    <td>{translationPdp}</td>
                    <td>{translationPseudo}</td>
                    <td>{translationAdmin}</td>
                    <td></td>
                </tr>
            </thead>
            <tbody>
                {(allUsersData && (
                    allUsersData.filter(([_, {userName, userPicture}]) => {
                        return userName.toLowerCase().includes(userSearch.toLowerCase())
                                || userPicture.toLowerCase().includes(userSearch.toLowerCase());
                    }).map(([id, {userName, userPicture, admin}]) => {
                        return (
                            <tr key={id}>
                                <td><img
                                        src={userPicture} alt=""
                                        referrerPolicy="no-referrer"
                                        style={{borderRadius: '15px', width: '40px', verticalAlign: 'middle'}}
                                    />
                                </td>
                                <td><Highlighter 
                                        searchWords={[userSearch.toLowerCase()]}
                                        textToHighlight={userName}
                                        highlightStyle={{borderRadius: '3px'}}
                                    />
                                </td>
                                <td>{admin ? translationOui : translationNon}</td>
                                <td><Button style={{paddingTop: '4px',
                                                    paddingBottom: '4px',
                                                    paddingRight: '10px',
                                                    paddingLeft: '10px',
                                                    verticalAlign: 'middle'}}
                                            onClick={() => basculerAdmin(id, admin)}>
                                        {translationBasculer}
                                    </Button>
                                </td>
                            </tr>
                        )
                    })
                ))}
            </tbody>
        </table>
        </>
    );
}

export default AdminUsersManager;
