import React from "react";

class WidthAnimatedTextDiv extends React.Component {
	constructor({ setSetter, children }) {
		super();
		this.state = {
			text: children,
		};
		setSetter(() => (text) => this.changeText(text));
		this.textRef = React.createRef();
	}

	changeText(text) {
		this.setState(
			{
				text,
			},
			() => {
				this.textRef.current.style.width = `${this.textRef.current.scrollWidth + 1}px`;
			}
		);
	}

	componentDidMount() {
		this.textRef.current.style.width = `${this.textRef.current.scrollWidth + 1}px`;
	}

	render() {
		return (
			<div>
				<div ref={this.textRef} className='width-animated-text-div'>
					{this.state.text}
				</div>
			</div>
		);
	}
}

export default WidthAnimatedTextDiv;
