import Select from 'react-select';

const colourStyles = (fontFamily) => {
    return {
        control: styles => ({ ...styles, width: '150px', fontFamily: {fontFamily} }),
        option: (styles, { data, isDisabled, isFocused, isSelected }) => {
            return {
            ...styles,
            border: '2px solid white',
            borderRadius: '10px',
            backgroundColor: isFocused ? 'var(--lighter-green)' : 'white',
            color: isFocused ? 'var(--white)' : 'black',
            marginBottom: '2px',
            marginTop: '2px',
            cursor: 'pointer',
            transition: 'background-color 0.15s ease-in-out, color 0.15s ease-in-out'
            };
        },
        menu: base => {
            return {
                ...base,
                backgroundColor: 'rgba(255, 255, 255, 0.15)',
                padding: '8px',
                border: '2px solid var(--green)',
                textAlign: 'left',
                fontFamily: fontFamily
            }
        }
    }
};

function Selection({options, defaultOption, handleChange, fontFamily}) {
    return (
        <Select
            defaultValue={defaultOption}
            options={options}
            className="selection-component"
            onChange={handleChange}
            styles={colourStyles(fontFamily)}
            hideSelectedOptions
        />
    )
}

export default Selection;
