import Loading from "../utils/Loading"
import useUserData from "../utils/useUserData"
import { useTranslation } from "react-i18next"
import database from '../utils/database'

function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    const { t } = useTranslation();

    if (isLoadingAuth) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (isLoadedUser && !userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    console.log(database.readDirectory('/users').then(obj => Object.entries(obj)).map(array => array[1]));

    return (
        <>
        TODO Admin page
        {/* Possibility to manage admin users ? */}
        </>
    );
}

export default Admin;
