const css = {
    'mobile': {},
    'tablet': {},
    'laptop': {},
    'desktop': {},
    'bigScreen' : {}
}

const updateCSS = (typeScreen) => {
    Object.entries(css[typeScreen]).forEach(([ variable, value ]) => {
        document.documentElement.style.setProperty(variable, value);
    });
}

export default updateCSS;
