import Modal from "react-modal";
import { GrFormClose } from "react-icons/gr";

const customStyles = {
	overlay: {
		// PAGE BEHIND THE MODAL
		backgroundColor: "rgba(255, 255, 255, 0.75)",
	},
	content: {
		backgroundColor: "var(--dark-gray)",
		border: "5px solid var(--orange-2)",
		boxShadow: "0 0 20px 3px black",
		// CENTERED CONTENT
		top: "50%",
		left: "50%",
		right: "auto",
		bottom: "auto",
		marginRight: "-50%",
		transform: "translate(-50%, -50%)",
	},
};

Modal.setAppElement("#root");

export default function ModalContact({ isOpen, setIsOpen, children }) {
	return (
		<Modal isOpen={isOpen} onRequestClose={() => setIsOpen((_) => false)} style={customStyles}>
			<GrFormClose className='modal-quit-button' onClick={() => setIsOpen((_) => false)} color="red"/>
			{children}
		</Modal>
	);
}
