import { useCallback, useState } from "react";
import { useTranslation } from "react-i18next";

import { motion } from "framer-motion";
import Button from "../utils/components/Button";
import LeftToRightComponents from "../utils/components/LeftToRightComponents";
import Loading from "../utils/components/Loading";

function Home() {
	const [number, setNumber] = useState(1);
	const [hasClickedOnce, setHasClickedOnce] = useState(false);
	const { t } = useTranslation();

	const [next, bindNext] = useState(() => () => {});
	const [previous, bindPrevious] = useState(() => () => {});

	let onClick = useCallback((_) => {
		setNumber((number) => number + 1);
		setHasClickedOnce(true);
	}, []);

	return (
		<div style={{ textAlign: "center" }}>
			<Button onClick={onClick}>Actuel : {number}</Button>

			{hasClickedOnce && (
				<motion.div
					animate={{ y: "20px", opacity: 1, scale: 1 }}
					initial={{ y: "+80vh", opacity: 0, scale: 0 }}
					transition={{ duration: 1.5 }}>
					{t("home_no_idea_message")}
				</motion.div>
			)}

			<Button onClick={previous}>Précédent</Button>
			<Button onClick={next}>Suivant</Button>

			<div
				style={{
					height: "400px",
					widht: '200px',
					textAlign: "center",
					display: "flex",
					flexDirection: "row",
					alignItems: "center",
					border: '1px solid red'
				}}>
				<LeftToRightComponents bindNext={bindNext} bindPrevious={bindPrevious} updateSelected={onClick}>
					<Loading fontSize={70} />
					<Loading />
				</LeftToRightComponents>
			</div>
		</div>
	);
}

export default Home;
