import { motion } from "framer-motion"

const fonduVariants = {
    show: {
      opacity: 1,
      width: '100%',
      height: '100%',
      transition: {
        delay: 1,
        duration: 1.2,
        ease: [0.83, 0, 0.17, 1]
      }
    },
    hidden: {
      opacity: 0,
      width: '0%',
      height: '0%',
      transition: {
        duration: 1.2,
        ease: [0.83, 0, 0.17, 1]
      }
    }
  }; 

function WrapFondu ({ app, show = true }) {
    return (
        <motion.div
        variants={fonduVariants}
        initial="hidden"
        exit="hidden"
        animate={show ? "show" : "hidden"}
        >
            {app}
        </motion.div>
    )
}

export {
    WrapFondu
}
