import { useTranslation } from "react-i18next";
import { useState, useEffect, useCallback } from "react";
import { RiDeleteBack2Fill } from "react-icons/ri";
import Highlighter from "react-highlight-words";

import Loading from "../utils/components/Loading";
import useUserData from "../utils/hooks/useUserData";
import database from "../utils/database/database";
import Button from "../utils/components/Button";

function AdminUsersManager() {
	const { isLoadingAuth, isLoadedUser } = useUserData();

	const { t } = useTranslation();

	const [allUsersData, setAllUsersData] = useState([]);

	const [userSearch, setUserSearch] = useState("");

	useEffect(() => {
		async function loadData() {
			database.readDirectory("/users").then((obj) => setAllUsersData(Object.entries(obj)));
		}
		loadData();
	}, []);

	const basculerAdmin = useCallback(
		(id, adminActuel) => {
			setAllUsersData(
				allUsersData.map(([identifiant, { admin, ...rest }]) => {
					if (id === identifiant) {
						return [identifiant, { admin: !admin, ...rest }];
					} else {
						return [identifiant, { admin, ...rest }];
					}
				})
			);
			database.setUserData(id, "admin", !adminActuel);
		},
		[allUsersData]
	);

	const search = useCallback((event) => {
		setUserSearch(event.target.value);
	}, []);

	if (isLoadingAuth || !isLoadedUser) {
		return <Loading />;
	}

	const {
		translationPdp,
		translationPseudo,
		translationAdmin,
		translationOui,
		translationNon,
		translationBasculer,
		translationUserList,
		translationRechercher,
	} = t("admin_page_users", { returnObjects: true });

	return (
		<div style={{ textAlign: "center" }}>
			<h3 style={{ marginBottom: "20px", marginTop: "0" }}>{translationUserList}</h3>
			<div style={{ display: "inline-block" }}>
				<div
					style={{
						display: "flex",
						flexDirection: "row",
						alignItems: "center",
						flexFlow: "row",
						marginBottom: "10px",
					}}>
					<input
						onChange={search}
						value={userSearch}
						placeholder={translationRechercher}
						style={{ marginRight: "-30px" }}
					/>
					<RiDeleteBack2Fill
						style={{ color: "var(--orange)", cursor: "pointer" }}
						onClick={() => setUserSearch("")}
					/>
				</div>
			</div>
			<table className='pretty-table'>
				<thead>
					<tr>
						<td>{translationPdp}</td>
						<td>{translationPseudo}</td>
						<td>{translationAdmin}</td>
						<td></td>
					</tr>
				</thead>
				<tbody>
					{allUsersData &&
						allUsersData
							.filter(([_, { userName, userPicture }]) => {
								return (
									userName.toLowerCase().includes(userSearch.toLowerCase()) ||
									userPicture.toLowerCase().includes(userSearch.toLowerCase())
								);
							})
							.map(([id, { userName, userPicture, admin }]) => {
								return (
									<tr key={id}>
										<td>
											<img
												src={userPicture}
												alt=''
												referrerPolicy='no-referrer'
												style={{ borderRadius: "15px", width: "40px", verticalAlign: "middle" }}
											/>
										</td>
										<td>
											<Highlighter
												searchWords={[userSearch.toLowerCase()]}
												textToHighlight={userName}
												highlightStyle={{ borderRadius: "3px" }}
											/>
										</td>
										<td>{admin ? translationOui : translationNon}</td>
										<td>
											<Button
												style={{
													paddingTop: "4px",
													paddingBottom: "4px",
													paddingRight: "10px",
													paddingLeft: "10px",
													verticalAlign: "middle",
												}}
												onClick={() => basculerAdmin(id, admin)}>
												{translationBasculer}
											</Button>
										</td>
									</tr>
								);
							})}
				</tbody>
			</table>
		</div>
	);
}

export default AdminUsersManager;
