import { Fragment } from "react";
import Loading from "../utils/Loading";
import useUserData from "../utils/useUserData";
import CustomSelectProps from "./settings/Language";

function Settings() {
    
    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    if (isLoadingAuth) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <Fragment>Veuillez vous connecter pour modifier vos paramètres.</Fragment>
    }

    // L'utilisateur est authentifié

    return (
        !isLoadedUser ? <Loading /> : (
            <>
            <LanguageSelectorField userData={userData}/>
            </>
        )
    )

}

export default Settings;
