import { motion } from "framer-motion";
import { useState } from "react"

function Home() {

    const [ number, setNumber ] = useState(1);
    const [ hasClickedOnce, setHasClickedOnce ] = useState(false);

    let onClick = (_) => {
        setNumber(number + 1);
        setHasClickedOnce(true);
    }

    return (
        <div>
            <button onClick={onClick}>
                Actuel : {number}
            </button>

            { hasClickedOnce ?
            <motion.div
                animate={{y: '20px', opacity: 1, scale: 1}}
                initial={{y: '+80vh', opacity: 0, scale: 0}}
                transition={{duration: 1.5}}
            >
                Ouais bon... j'avais pas d'id√©e...
            </motion.div>
            : ""}

        </div>
    )

}

export default Home;
