import React from "react";
import { forwardRef } from "react";
import { useMemo } from "react";
import { useCallback, useState } from "react";
import { useEffect, useImperativeHandle } from "react";

const DIRECTION = {
	VERS_GAUCHE: -1,
	VERS_DROITE: 1,
};

const LeftToRightComponents = forwardRef(
	(
		{
			children,
			bindNext,
			bindPrevious,
			durationInMs = 500,
			translationPercentOfScreen = 30,
			updateSelectedOnParent,
		},
		ref
	) => {
		const [selected, setSelected] = useState(0);

		const childrenArray = useMemo(() => React.Children.toArray(children), [children]);
		const [currentChild, setCurrentChild] = useState(null);
		const [direction, setDirection] = useState(null);
		const [etape, setEtape] = useState(2);

		const max = useMemo(() => React.Children.count(children) - 1, [children]);

		const next = useCallback(() => {
			setDirection((_) => DIRECTION.VERS_DROITE);
			setSelected((selected) => (selected < max ? selected + 1 : selected));
		}, [max]);

		const previous = useCallback(() => {
			setDirection((_) => DIRECTION.VERS_GAUCHE);
			setSelected((selected) => (selected > 0 ? selected - 1 : selected));
		}, []);

		useEffect(() => {
			bindNext(() => next);
			bindPrevious(() => previous);
		}, [bindNext, bindPrevious, next, previous]);

		useEffect(() => {
			updateSelectedOnParent([selected, max].map((i) => i + 1));
		}, [selected, max, updateSelectedOnParent]);

		useEffect(() => {
			setEtape(1);
			setTimeout(() => {
				setEtape(1.5);
				setCurrentChild(childrenArray[selected]);
				setTimeout(() => setEtape(2), 1);
			}, durationInMs);
		}, [selected, childrenArray, durationInMs]);

		useImperativeHandle(
			ref,
			() => ({
				isFirst() {
					return selected === 0;
				},
				isLast() {
					console.log('islast')
					return selected === max;
				}
			}),
			[selected, max]
		);

		const getTranslation = useCallback(() => {
			return etape === 2
				? "0"
				: direction === DIRECTION.VERS_DROITE
				? etape !== 1.5
					? `-${translationPercentOfScreen}vw`
					: `${translationPercentOfScreen}vw`
				: etape !== 1.5
				? `${translationPercentOfScreen}vw`
				: `-${translationPercentOfScreen}vw`;
		}, [etape, direction, translationPercentOfScreen]);

		const getOpacity = useCallback(() => {
			return etape !== 2 ? 0 : 1;
		}, [etape]);

		return (
			<div
				style={{
					transition:
						etape !== 1.5
							? `transform ${durationInMs}ms ease-in-out, opacity ${durationInMs}ms ease-in-out`
							: `opacity ${durationInMs}ms ease-in-out`,
					transform: `translateX(${getTranslation()})`,
					opacity: getOpacity(),
				}}
				children={currentChild}
			/>
		);
	}
);

export default LeftToRightComponents;
