import { useRef } from "react";
import { useState } from "react";

import LeftToRightComponents from "../utils/components/LeftToRightComponents";
import { IoChevronBack, IoChevronForward } from "react-icons/io5";

function Home() {
	const [selected, setSelected] = useState(null);

	const childRef = useRef();

	return (
		<>
		<div style={{width: '50%'}}>
			<div style={{ width: '100%', display: "flex", alignItems: 'stretch', flexDirection: 'row' }}>
				{/* <button
				onClick={childRef.current && childRef.current.previous}
				style={{ marginRight: "15px", width: "140px" }}
				{...(childRef.current && childRef.current.isFirst() && { disabled: true })}>
				Précédent
			</button>
			<button
			onClick={childRef.current && childRef.current.next}
				style={{ marginLeft: "15px", width: "140px" }}
				{...(childRef.current && childRef.current.isLast() && { disabled: true })}>
				Suivant
			</button> */}
				<span
					onClick={() => childRef.current && !childRef.current.isFirst() && childRef.current.previous()}
					style={{
						border: "1px solid red",
						display: "flex",
						flexDirection: 'row',
						alignItems: "center",
						justifyContent: 'center'
					}}>
					<IoChevronBack />
				</span>
				<LeftToRightComponents
					height='50vh'
					ref={childRef}
					border='1px solid red'
					flex={1}
					durationInMs={200}
					updateSelectedOnParent={(res) => setSelected(res.join("/"))}>
					<>Page 1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</>
					<>Page 2</>
					<>Page 3</>
				</LeftToRightComponents>
				<span
					onClick={() => childRef.current && !childRef.current.isLast() && childRef.current.next()}
					style={{
						border: "1px solid red"
					}}>
					<IoChevronForward style={{margin: '0 auto'}}/>
				</span>
			</div>
			</div>
			Page {selected}
		</>
	);
}

export default Home;
