import { IoLanguageOutline } from 'react-icons/io5'

import Selection from '../../utils/components/Selection';
import info from '../../languages/info';
import { changeLanguage } from 'i18next';

const options = info.map(({language, label}) => {
	return { value: language, label };
});

function LanguageButton() {
    return (
        <Selection
            options={options}
            defaultOption='fr'
            handleChange={(lang => changeLanguage(lang))}
        >
            <IoLanguageOutline className='language-button'/>
        </Selection>
    )
}

export default LanguageButton;
