function CopyText({ children, className = "", overrideText, ...rest }) {
	return (
		<span
			{...rest}
			className={`copy-text ${className}`}
			onClick={() => navigator.clipboard.writeText(overrideText ? overrideText : children).catch(() => {})}>
			{children}
		</span>
	);
}

export default CopyText;
