import { useCallback, useState } from "react";
import { useTranslation } from "react-i18next";

import { motion } from "framer-motion";
import Button from "../utils/components/Button";

function Home() {
	const [number, setNumber] = useState(1);
	const [hasClickedOnce, setHasClickedOnce] = useState(false);
	const { t } = useTranslation();

	const [ isHovered, setIsHovered ] = useState(false);

	let onClick = useCallback((_) => {
		setNumber((number) => number + 1);
		setHasClickedOnce(true);
	}, []);

	return (
		<div style={{ textAlign: "center" }}>
			<Button onClick={onClick}>Actuel : {number}</Button>

			{hasClickedOnce && (
				<>
					<motion.div
						style={{margin: '20px'}}
						animate={{ opacity: 1, scale: 1, rotate: 0 }}
						initial={{ opacity: 0, scale: .4, rotate: '180deg' }}
						transition={{ duration: 1.3, type: "spring", damping: 9 }}>
						{t("home_no_idea_message")}
					</motion.div>
					<motion.svg
						width='50px'
						height='50px'
					>
						<motion.circle
							cx="25"
							cy="25"
							r="22"
							className="test"
							initial={{pathLength: 0, opacity: 0}}
							animate={{pathLength: 1, opacity: 1}}
							transition={{duration: 1}}
							onMouseEnter={() => setIsHovered(true)}
							onMouseLeave={() => setIsHovered(false)}
						/>
						<motion.polyline
							points="15 23 22 30 35 17"
							fill="none"
							initial={{pathLength: 0, opacity: 0}}
							animate={isHovered ? {pathLength: 1, opacity: 1} : {pathLength: 0, opacity: 0}}
							transition={{duration: 0.5, delay: isHovered ? 0.2 : 0}}
							stroke="var(--green)"
							strokeWidth="5px"
						/>
					</motion.svg>
				</>
			)}
		</div>
	);
}

export default Home;
