import Modal from "react-modal";

const customStyles = {
	overlay: { // PAGE BEHIND THE MODAL
		backgroundColor: "rgba(255, 255, 255, 0.75)",
	},
	content: {
		backgroundColor: "var(--dark-gray)",
		border: "3px solid var(--orange-2)",
		// CENTERED CONTENT
		top: "50%",
		left: "50%",
		right: "auto",
		bottom: "auto",
		marginRight: "-50%",
		transform: "translate(-50%, -50%)",
	},
};

Modal.setAppElement("#root");

export default function ModalContact({ isOpen, setIsOpen, children }) {
	return (
		<Modal isOpen={isOpen} onRequestClose={() => setIsOpen((_) => false)} style={customStyles}>
			{/* TODO: X button */}
			{children}
		</Modal>
	);
}
