import AdminUsersManager from "./AdminUsersManager"
import useUserData from "../utils/useUserData"
import Loading from "../utils/Loading"
import { useTranslation } from "react-i18next"
import { useCallback, useState } from "react"
import { AnimatePresence, motion } from "framer-motion"

const appearVariant = {
    show: {
        opacity: 1,
        height: '100%',
        transition: {
            delay: 1,
            duration: 1,
            ease: [0.83, 0, 0.17, 1],
            opacity: {
                delay: 1.7,
                duration: 0.5
            }
        }
    },
    hidden: {
        opacity: 0,
        height: '0',
        transition: {
            duration: 0.5,
            height: {
                delay: 0.3,
                duration: 1,
                ease: [0.83, 0, 0.17, 1]
            }
        }
    }
}; 

function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    const [ toShow, setToShow ] = useState(false);

    const [ selected, setSelected ] = useState(0);

    const { t } = useTranslation();

    const toggleShow = useCallback(() => {
        setToShow(toShow => !toShow);
    }, [])

    if (isLoadingAuth || !isLoadedUser) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (isLoadedUser && !userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    return <>
        <button onClick={toggleShow}>LOL</button>
        <AnimatePresence>
            {selected === 0 ?
            <motion.div
                    key='0'
                    variants={appearVariant}
                    exit='hidden'
                    initial='hidden'
                    animate={toShow ? 'show' : 'hidden'}
                    style={{textAlign: 'center'}}
            >
                <AdminUsersManager />
            </motion.div>
            :
            <motion.div
                    key='1'
                    variants={appearVariant}
                    exit='hidden'
                    initial='hidden'
                    animate={toShow ? 'show' : 'hidden'}
                    style={{textAlign: 'center'}}
            >
                <Loading />
            </motion.div>
            }
        </AnimatePresence>
        <button onClick={() => setSelected(selected => selected === 0 ? 1 : 0)}>BOUTON</button>
        <button onClick={() => setSelected(selected => selected === 0 ? 1 : 0)}>BOUTON</button>
        <button onClick={() => setSelected(selected => selected === 0 ? 1 : 0)}>BOUTON</button>
        <button onClick={() => setSelected(selected => selected === 0 ? 1 : 0)}>BOUTON</button>
        <button onClick={() => setSelected(selected => selected === 0 ? 1 : 0)}>BOUTON</button>
        </>
}

export default Admin;
