import Loading from "../utils/Loading"
import useUserData from "../utils/useUserData"
import { useTranslation } from "react-i18next"

function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData, userID } = useUserData();

    const { t } = useTranslation();

    if (isLoadingAuth) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (!userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    return (
        <>
        TODO Admin page
        </>
    );
}

export default Admin;
