import AdminUsersManager from "./AdminUsersManager"
import useUserData from "../utils/useUserData"
import Loading from "../utils/Loading"
import { useTranslation } from "react-i18next"
import { useState } from "react"
import FadeProps from 'fade-props'
import ButtonsSelector from "../utils/ButtonsSelector"

function Admin() {

    const { isLoadingAuth, isLoadedUser, isAuthenticated, userData } = useUserData();

    const [ selected, setSelected ] = useState(null);

    const { t } = useTranslation();

    if (isLoadingAuth || !isLoadedUser) {
        return <Loading />
    }

    if (!isAuthenticated) {
        return <>{t('unauthentified_message_admin_page')}</>
    }

    if (isLoadedUser && !userData.admin) {
        return <>{t('unauthorized_message_admin_page')}</>
    }

    return <>
        <ButtonsSelector setSelected={setSelected}>LOL</ButtonsSelector>
        <FadeProps>
            {selected != null && (selected===0 ? <AdminUsersManager /> : <Loading />)}
        </FadeProps>
        </>
}

export default Admin;
