import { useEffect, useState, Fragment } from "react";
import { useNavigate } from "react-router";
import { SiBandsintown } from "react-icons/si";
import { useAuth0 } from "@auth0/auth0-react";
import { FiLogIn } from "react-icons/fi";
import { useTranslation } from "react-i18next";

import Logo from "./utils/components/Logo";
import Loading from "./utils/components/Loading";
import { motion } from "framer-motion";
import useUserData from "./utils/hooks/useUserData";

function NavigationBar(props) {
	const navigate = useNavigate();
	const { user, isLoading, isAuthenticated, loginWithRedirect, logout } = useAuth0();
	const { isLoadedUser, userData } = useUserData();

	const { t } = useTranslation();

	const pagesList = props.pagesList;
	const [currentPageName, setCurrentPageName] = useState(props.currentPageName);

	const loginIcon = <FiLogIn style={{ verticalAlign: "top", paddingTop: "10%" }} />;

	useEffect(() => {
		setCurrentPageName(props.currentPageName);
	}, [props.currentPageName]);

	const AuthAction = () => {
		if (!isLoading) {
			const [label, state, icon, action] = isAuthenticated
				? [
						t("disconnect_label"),
						"logout",
						<img
							src={user.picture}
							style={{
								verticalAlign: "top",
								marginTop: "10%",
								borderRadius: "50%",
								border: "2px solid gray",
								width: "calc(2 * var(--navbar-item-text-font-size))",
							}}
							referrerPolicy='no-referrer'
							alt=''
						/>,
						() => logout({ returnTo: window.location.origin + "/home" }),
				  ]
				: [t("connection_label"), "login", loginIcon, loginWithRedirect];
			return (
				<li key={state} className='right' onClick={() => action()}>
					<div className='nav-bar-item'>
						<div className='nav-bar-item-icon'>{icon}</div>
						<div className='nav-bar-item-text'>{label}</div>
					</div>
				</li>
			);
		} else {
			return (
				<li key={"loadingauth0"} className='right'>
					<div className='nav-bar-item'>
						<div className='nav-bar-item-icon'>
							<Loading
								height={"calc(var(--navigation-bar-height) * 0.8)"}
								width={"calc(var(--navigation-bar-height) * 0.8)"}
							/>
						</div>
						<div className='nav-bar-item-text'></div>
					</div>
				</li>
			);
		}
	};

	return (
		<Fragment>
			<motion.div
				exit={{ y: "-100%" }}
				animate={{ y: 0 }}
				initial={{ y: "-100%" }}
				transition={{ duration: 0.5, delay: 0.6 }}>
				<div className='navigation-bar'>
					<ul>
						{/* Le logo de l'app */}
						<li style={{ float: "left" }}>
							<Logo />
						</li>
						<AuthAction />
						{/* La liste des pages Ã  droite */}
						{pagesList
							.slice()
							.reverse()
							.filter((page) => !page.hidden)
							.filter(
								({ admin }) =>
									!admin || (admin && !isLoading && isAuthenticated && isLoadedUser && userData.admin)
							)
							.map(({ name, path, icon }) => {
								return (
									<li
										key={name}
										className={name === currentPageName ? "active right" : "right"}
										onClick={() => navigate(path)}>
										<div className='nav-bar-item'>
											<div className='nav-bar-item-icon'>{icon}</div>
											<div className='nav-bar-item-text'>{t(name)}</div>
										</div>
									</li>
								);
							})}
					</ul>
				</div>
			</motion.div>
			<motion.div
				exit={{ x: "+130%" }}
				animate={{ x: 0 }}
				initial={{ x: "-110%" }}
				transition={{ duration: 1.5 }}>
				{currentPageName ? (
					<div className='left-title'>
						<SiBandsintown
							style={{
								marginLeft: "5vw",
								marginRight: "10vw",
								fontSize: "var(--title-icon-size)",
								color: "var(--orange)",
							}}
						/>
						{t(pagesList.find((page) => page.name === currentPageName).title)}
					</div>
				) : (
					""
				)}
			</motion.div>
		</Fragment>
	);
}

export default NavigationBar;
