import { useRef } from "react";
import { useState } from "react";

import Button from "../utils/components/Button";
import LeftToRightComponents from "../utils/components/LeftToRightComponents";

function Home() {
	const [next, bindNext] = useState(() => () => {});
	const [previous, bindPrevious] = useState(() => () => {});

	const [selected, setSelected] = useState(null);

	const childRef = useRef();

	return (
		<div style={{ textAlign: "center" }}>
			<Button onClick={previous} style={{ marginRight: "15px", width: "140px" }}
			{...(childRef.current && {disabled: childRef.current.isLast})}>
				Précédent
			</Button>
			<Button onClick={next} style={{ marginLeft: "15px", width: "140px" }}>
				Suivant
			</Button>
			<div
				style={{
					height: "50vh",
					justifyContent: "center",
					display: "flex",
					flexDirection: "row",
					alignItems: "center",
				}}>
				<LeftToRightComponents
					ref={childRef}
					bindNext={bindNext}
					bindPrevious={bindPrevious}
					durationInMs={200}
					updateSelectedOnParent={(res) => setSelected(res.join("/"))}>
					<>Page 1 aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</>
					<>Page 2</>
					<>Page 3</>
				</LeftToRightComponents>
			</div>
			Page {selected}
		</div>
	);
}

export default Home;
