import CircularProgress from "react-cssfx-loading/lib/CircularProgress";
import { useState } from "react";
import { motion } from "framer-motion";

function useLoaded(loadedRender) {

    const [ isLoaded, setIsLoaded ] = useState(false);

    let render = () => {
        if (!isLoaded) {
            return <motion.div
            exit={{opacity: 0}}
            animate={{opacity: 1}}
            initial={{opacity: 0}}
            transition={{duration: 0.8}}
            ><CircularProgress color='orange'/></motion.div>;
        } else {
            return <motion.div
            exit={{opacity: 0}}
            animate={{opacity: 1}}
            initial={{opacity: 0}}
            transition={{duration: 0.8}}
            >{loadedRender()}</motion.div>;
        }
    }

    return [ isLoaded, setIsLoaded, render ];

}

export default useLoaded;
