import { useCallback, useEffect, useMemo, useState } from "react";

function Track({ trackHeight, scrollTop, scrollTopMax, cappedHeight, totalHeight }) {
	const initialTopAndY0 = useMemo(() => ({ top: 0, y0: null }), []);

	const [topAndY0, setTopAndY0] = useState(initialTopAndY0);

	useEffect(() => {
		setTopAndY0(({ y0 }) => ({
			top: ((scrollTop / scrollTopMax) * (cappedHeight - trackHeight)) | 0,
			y0: y0,
		}));
	}, [scrollTop, scrollTopMax, cappedHeight, trackHeight]);

	const onMouseMove = useCallback(
		(e) => {
			e.preventDefault();
			setTopAndY0(({ top, y0 }) => {
				const newTop = (() => {
					const newTop = (top + e.clientY - y0) / 2;
					if (newTop < 0) {
						return 0;
					} else if (newTop > cappedHeight - trackHeight + 2) {
						return cappedHeight - trackHeight + 2;
					} else {
						return newTop;
					}
				})();
				return { top: newTop, y0: y0 };
			});
		},
		[cappedHeight, trackHeight]
	);

	const onMouseUp = useCallback(
		(_) => {
			window.removeEventListener("mousemove", onMouseMove);
			window.removeEventListener("mouseup", onMouseUp);
		},
		[onMouseMove, initialTopAndY0]
	);

	const onMouseDown = useCallback(
		(e) => {
			e.preventDefault();
			setTopAndY0(({ top }) => ({ top: top, y0: e.clientY }));
			window.addEventListener("mousemove", onMouseMove);
			window.addEventListener("mouseup", onMouseUp);
		},
		[onMouseMove, onMouseUp]
	);

	return (
		<div
			className='track'
			style={{
				width: "100%",
				height: trackHeight && trackHeight !== Infinity ? trackHeight : 0,
				top: topAndY0.top,
			}}
			onMouseDown={onMouseDown}
		/>
	);
}

function Scrollbar({ totalHeight, cappedHeight, scrollTop, scrollTopMax }) {
	const [trackHeight, setTrackHeight] = useState(null);

	useEffect(() => {
		setTrackHeight((cappedHeight * cappedHeight) / totalHeight);
	}, [cappedHeight, totalHeight]);

	return (
		<div className='scrollbar'>
			<Track
				trackHeight={trackHeight}
				totalHeight={totalHeight}
				cappedHeight={cappedHeight}
				scrollTop={scrollTop}
				scrollTopMax={scrollTopMax}
			/>
		</div>
	);
}

function WithScrollbarComponent({ Child }) {
	const [totalHeight, setTotalHeight] = useState(null);
	const [cappedHeight, setCappedHeight] = useState(null);
	const [scrollTop, setScrollTop] = useState(null);
	const [scrollTopMax, setScrollTopMax] = useState(null);

	return (
		<div className='scrollable'>
			<Child
				ref={(node) =>
					node &&
					setTimeout(() => {
						node.clientHeight && setCappedHeight(node.clientHeight);
						node.scrollHeight && setTotalHeight(node.scrollHeight);
					}, 2)
				}
				onScroll={(e) => {
					setScrollTop(e.target.scrollTop);
					setScrollTopMax(e.target.scrollTopMax);
				}}
			/>
			<Scrollbar
				totalHeight={totalHeight}
				cappedHeight={cappedHeight}
				scrollTop={scrollTop}
				scrollTopMax={scrollTopMax}
			/>
		</div>
	);
}

export default function withScrollbar(Child) {
	return () => <WithScrollbarComponent Child={Child} />;
}
