import { Component } from "react"
import "./style.css"
import { Link } from "react-router-dom";

class NavigationBar extends Component {
    
    state = {
        pagesList: this.props.pagesList,
        currentPageName: this.props.currentPageName
    }

    useEffect = this.useEffect(() => {
        this.setCurrentPageName(this.props.currentPageName)
    }, [this.props.currentPageName])

    setCurrentPageName = (name) => {
        this.setState({currentPageName: name})
    }

    render() {
        return (
            <ul className="navigation-bar">
                {
                    this.state.pagesList.slice().reverse().map(({name, path}) => {
                        return (
                            <li key={name} className={name === this.state.currentPageName ? "active" : ""}>
                                <Link to={path}>
                                    {name}
                                </Link>
                            </li>
                        )
                    })
                }
            </ul>
        )
    }

}

export default NavigationBar;
