import { IoLanguageOutline } from 'react-icons/io5'

import info from '../../languages/info';
import { changeLanguage } from 'i18next';
import Select from 'react-select/dist/declarations/src/Select';

const options = info.map(({language, label}) => {
	return { value: language, label };
});

const colourStyles = {
	control: (styles) => ({
		...styles,
		fontFamily: "var(--coman-font)",
		textAlign: "center",
		backgroundColor: "var(--white)",
	}),
	option: (styles, { data, isDisabled, isFocused, isSelected }) => {
		return {
			...styles,
			border: "2px solid var(--white)",
			borderRadius: "10px",
			backgroundColor: isFocused ? "var(--green-1)" : "var(--white)",
			color: isFocused ? "var(--white)" : "black",
			marginBottom: "2px",
			marginTop: "2px",
			cursor: "pointer",
			transition: "background-color 0.15s ease-in-out, color 0.15s ease-in-out",
		};
	},
	menu: (base) => {
		return {
			...base,
			backgroundColor: "rgba(0, 0, 0, 0.45)",
			padding: "8px",
			border: "2px solid var(--green-3)",
			textAlign: "center",
			fontFamily: "var(--coman-font)",
		};
	},
};

const Control = ({ children, ...props }) => (
	<components.Control {...props}>
		<button><IoLanguageOutline /></button>
	</components.Control>
);

function LanguageButton() {
    return (
        <Select
            options={options}
            defaultValue='fr'
            onChange={(({value}) => changeLanguage(value))}
            className='language-button'
        >
            <IoLanguageOutline />
        </Select>
    )
}

export default LanguageButton;
