import React from "react";
import { useMemo } from "react";
import { useCallback, useState } from "react";

import { useEffect } from "react";

const DIRECTION = {
	DEPUIS_GAUCHE: 1,
	VERS_DROITE: 2,
	VERS_GAUCHE: -1,
	DEPUIS_DROITE: -2,
};

const direction = "0";

function LeftToRightComponents({ children, bindNext, bindPrevious }) {
	const [selected, setSelected] = useState(0);

	const childrenArray = React.Children.toArray(children);

	const max = useMemo(() => React.Children.count(children) - 1, [children]);

	const next = () => {
		setSelected((selected) => (selected < max ? selected + 1 : selected));
	};

	const previous = useCallback(() => {
		setSelected((selected) => (selected > 0 ? selected - 1 : selected));
	}, []);

	useEffect(() => {
		console.log(next.prototype)
	}, [bindNext, bindPrevious, next, previous]);

	useEffect(() => {
		console.log(selected);
	}, [selected]);

	return (
		<div
			style={{
				transition: `transform 1s ease-in-out`,
				transform: `translateX(${direction})`,
			}}
			children={
				<>
					{selected} et {childrenArray[selected]}
				</>
			}
		/>
	);
}

export default LeftToRightComponents;
